css:
* {
  color: black;
}

table {
  border: 1px solid transparent;
}

.print-heading {
  display: none;
}

.page-header,
.page-header-space {
  /*height: 150px!important;*/
  /*padding-bottom:150px!important*/
}

.page-footer,
.page-footer-space {
  /*height: 120px; */
}

.page-footer {
  position: fixed;
  bottom: 0;
  width: 100%;
  margin: auto;
}

.page-break-before {
  page-break-before: always;
}

.page-header {
  position: fixed;
  top: 0mm;
  display: block;
  width: 100%;
  border-bottom: 0 !important;
  color: #ffffff;
  margin-left: 0px !important;
  padding-left: 0 !important;
}

.print-format .print-format-preview {
  padding-left: 0px !important;
}

.test-table {
  color: black !important;
  width: 100%;
  border-collapse: collapse;
}

.test-table td {
  text-align: left;
  /*border-top: 1px solid black; */
  border-bottom: 1px solid black;
  border-left: 1px solid black;
  border-right: 1px solid black;
}

.test-table td:nth-child(1) {
  width: 20%;
  min-width: 150px;
}

.test-table td:nth-child(2) {
  width: 20%;
  min-width: 150px;
}

.test-table td:nth-child(3) {
  width: 60%;
}

@page {
  size: A4 potrait;
  margin: 10mm;
  margin-top: 2055mm;
}

@media print {
  thead {
    display: table-header-group;
  }

  tfoot {
    display: table-footer-group;
  }

  .page-header,
  .page-header-space {
    height: 66px;
    border-bottom: 0 !important;
  }

  .page-header,
  .page-footer-space {
    /*height:120px;*/
    height: 40px;
    /*margin-top:400px;*/
  }

  @media print {
    .page-break-before {
      page-break-before: always;
    }

    .page-header {
      display: block;
      /*height: 300px;*/
      /*margin-bottom: 300px;*/
    }

    .print-format .print-format-preview {
      padding-left: 0px !important;
      padding-top: 0px !important;
    }
  }

  button {
    display: none;
  }

  body {
    margin: 0;
  }
}

.page-header {
  margin-top: 0px !important;
  height: 300px;
  margin-bottom: 300px;
}

.text-center h4 {
  display: none;
}

body {
  counter-reset: page;
}

.page-no::after {
  counter-increment: page;
  content: "Page " counter(page);
}

